mapper.select:
	select *
	from(
	select nvl(search_ttl,0) as searchTtl, 
			count(search_ttl) as ttlCnt
	    from search
	    group by search_ttl
	    order by ttlcnt desc
	    )

        where rownum <= 10


        and pst_se = #{pstSe}


        and stts = #{stts}

mapper.update:
update search
set stts = #{stts}
where search_ttl = #{searchTtl}
