mapper.select:
 	SELECT b.*, ROWNUM AS rnum 
				FROM (
				    SELECT a.*, ROWNUM AS rn
				    FROM (
				        SELECT d.* ,
				               (SELECT ATCH_PATH
				                FROM ATCH
				                WHERE ATCH_ID = d.ATCH_ID AND ROWNUM=1) ATCH_NM
				                , m.atch_path profile, m.nick_nm nick_Nm, m.addr addr 
				        FROM deal d JOIN MEM m 
				        ON d.ntsl_Id = m.user_id


				             ntsl_id = #{ntslId}


				             and stts = #{stts} 


				             and prchs_id = #{prchsId}



				    	) a WHERE ROWNUM <= #{last}) b WHERE rn >= #{first}


	    select * 
		from (select rownum rn,a.* from(
			SELECT d.* ,
	       (SELECT ATCH_PATH
       		FROM ATCH
       		WHERE ATCH_ID = d.ATCH_ID AND ROWNUM=1) ATCH_NM 
		FROM DEAL d
		where ntsl_id = #{ntslId}


	) a where rownum <= #{last} )b where rn >= #{first}

select * 
		from (select rownum rn,a.* from(
		SELECT d.* ,
	       (SELECT ATCH_PATH
       		FROM ATCH
       		WHERE ATCH_ID = d.ATCH_ID AND ROWNUM=1) ATCH_NM 
		FROM DEAL d


		order by ${orderby} desc


		order by dl_ymd desc


	) a where rownum <= #{last} )b where rn >= #{first}

mapper.update:
UPDATE DEAL
SET 
	 DL_TTL= #{dlTtl},
	 DL_CN = #{dlCn},
	 CTGRY = #{ctgry},
	 DL_YMD = CURRENT_DATE,
	 DL_PRC = #{dlPrc},
	 AREA = #{area}
	,NEGO_YN = #{negoYn}
WHERE DL_NO = #{dlNo}

UPDATE DEAL
SET INQ_CNT = INQ_CNT + 1
WHERE DL_NO = #{dlNo}

update deal
set dl_ymd = current_date
where dl_no = #{dlNo} and ntsl_id = #{ntslId}

