mapper.select:
SELECT ATCH_ID
		FROM DEAL
		WHERE DL_NO = #{dlNo}

select * 
		from (select rownum rn,a.* from(
		SELECT d.* ,
	       (SELECT ATCH_PATH
       		FROM ATCH
       		WHERE ATCH_ID = d.ATCH_ID AND ROWNUM=1) ATCH_NM 
		FROM DEAL d


		order by dl_no

	) a where rownum <= #{last} )b where rn >= #{first}

mapper.update:
UPDATE DEAL
SET INQ_CNT = INQ_CNT + 1
WHERE DL_NO = #{dlNo}
