mapper.select:
   		SELECT *
		FROM (
		    SELECT ROWNUM rn, a.*
		    FROM (
		        SELECT r.*
		        FROM rv r
		        WHERE dl_no IN (
		            SELECT dl_no
		            FROM deal
		            WHERE ntsl_id = #{ntslId}
		        )

		    ) a WHERE ROWNUM <= #{last}) b WHERE rn >= #{first}
