mapper.select:
SELECT * FROM sns_cmnt
WHERE sns_no= #{snsNo}
ORDER BY sns_no,recmnt_order

mapper.insert:
SELECT NVL(MAX(cmnt_no),0)+1 AS cmntNo FROM SNS_CMNT
INSERT INTO SNS_CMNT ( cmnt_no, sns_no, cmnt_cn, cmnt_mem, cmnt_ymd, group_no
) VALUES ( #{cmntNo}, #{snsNo}, #{cmntCn}, #{cmntMem}, CURRENT_Date, #{cmntNo}
)
